import React from "react";
import './Form.css';


function Form({ name }) {
    const [comming, setComming] = React.useState(null);
    const [clicked, setClicked] = React.useState(null);

    let _handleRadio = (event) => {
        const comming = event.currentTarget.value === 'true' ? true : false;
        setComming(comming)
        setClicked(comming);
    }

    let _handleSubmit = async (event) => {
        event.preventDefault();
        event.stopPropagation();
    }

    return (
        <>
            {!clicked ?


                <form onSubmit={async (e) => await _handleSubmit(e)}>
                    < div className="radio" >
                        <label>
                            <input
                                type="radio"
                                name="comming"
                                value="true"
                                checked={comming === true}
                                onChange={_handleRadio} />
                            Ще присъствам
                        </label>
                    </div >
                    <div className="radio">
                        <label>
                            <input
                                type="radio"
                                name="comming"
                                value="false"
                                checked={comming === false}
                                onChange={_handleRadio} />
                            Няма да присъствам
                        </label>
                    </div>
                    <button type="submit">Потвърждавам избора си</button>
                    <h2>{name}</h2>

                </form > : <h1>clicked</h1>}
        </>
    );
}

export default Form;
